import axios from 'axios';
import {
    LOGIN_USER,
    REGISTER_USER,
    AUTH_USER,
    LOGOUT_USER,
    GET_USERS,
} from './types';

//async() is thunk function for delay
export const registerUser = (dataToSubmit) => async (dispatch) => {
    let responseData = await axios.post('http://localhost:5000/api/register', dataToSubmit)
    return dispatch({
        type: REGISTER_USER,
        payload: responseData
    })
}
export const loginUser = (dataToSubmit) => async (dispatch) => {
    const responseData = await axios.post('http://localhost:5000/api/login', dataToSubmit, {
        withCredentials: true,
    });
    return dispatch({
        type: LOGIN_USER,
        payload: responseData
    })
}
export const logoutUser = () => async (dispatch) => {
    const responseData = await axios.get('http://localhost:5000/api/logout', {
        withCredentials: true,
    })
    return dispatch({
        type: LOGOUT_USER,
        payload: responseData
    })
}

// withCredentials sets to true let us send the cookies stored over the browser to the server (like in api: req.cookie("jwt"))
